$(document).ready(function() {
    function copyTextToClipboard (text) {
      if (navigator.clipboard) {
        return  navigator.clipboard.writeText(text);
      }

      const isIos = navigator.userAgent.match(/ipad|iphone/i);
      const textarea = document.createElement('textarea');
      textarea.value = text;
      document.body.appendChild(textarea);
      if (isIos) {
        const range = document.createRange();
        range.selectNodeContents(textarea);
        const selection = window.getSelection();
        selection?.removeAllRanges();
        selection?.addRange(range);
        textarea.setSelectionRange(0, 999999);
      } else {
        textarea.select();
      }
      const result = document.execCommand('copy');
      document.body.removeChild(textarea);
      return result;
    }

    $('.copy-icon').on('click', function(e) {
      e.preventDefault();
      var copyText = $(this).data('copy-text');

$('.copy-icon').on('click', function(e) {
      e.preventDefault();
      var copyText = $(this).data('copy-text');

      document.addEventListener('copy', function(e) {
        e.clipboardData.setData('text/plain', copyText);
        e.preventDefault();
      }, true);

      $(".copy-icon img").animate({ opacity: 0 }, 300);
      $(".copy-icon svg").addClass('active');
      setTimeout(toDefault, 1500);
      function toDefault() {
        $(".copy-icon svg").removeClass('active');
        $(".copy-icon img").animate({ opacity: 1 }, 1500);
      }

      copyTextToClipboard(copyText);
    });
  });

