  $(document).ready(function(){
    function addComma(count){
       return [count].toString().replace(/(\d)(?=(\d{3})+$)/g, "$1,");
    }

    function changeText(sns, count){
      if(count!=0){
        $( sns['selector'] ).text(addComma(count));
      }
    }

function changeSocialCount(sns) {
      var params = {
        facebook: {
          url: 'https://graph.facebook.com?id=' + "https://gendai.ismedia.jp/articles/-/67246" + "&fields=og_object{engagement},engagement&access_token=229592243741014|GwhAFTnpEcYnu2_jIlMBxOztBvE",
          selector: '.facebook_count'
        },
        hatena_bookmark: {
          url: 'https://b.hatena.ne.jp/entry.count?url=' + "https://gendai.ismedia.jp/articles/-/67246",
          selector: '.hatena_bookmark_count'
        }
      }
      $.ajax({
        url: params[sns]['url'],
        dataType: 'jsonp',
        cache : false,
      }).done(function(res){
        if(/^\d*$/.exec(res)){ // hatena_bookmark
          changeText(params[sns], res);
        } else  if ( res.engagement ) { // facebook
          changeText(params[sns], res.engagement.reaction_count);
        } else {
          changeText(params[sns], 0);
        }
      }).fail(function(){
          changeText(params[sns], 0);
      });

} else {
          changeText(params[sns], 0);
        }
      }).fail(function(){
          changeText(params[sns], 0);
      });
    }
    changeSocialCount('facebook');
    changeSocialCount('hatena_bookmark');
  });

